import React, { useState, useEffect } from 'react'
import Datatable from './datatable'
import './App.css'

function App() {
  const [data, setData] = useState([])
  const [query, setQuery] = useState([])
  const [isLoading, setIsLoading] = useState(false)

  useEffect(() => {
    setIsLoading(true)
    Promise.all([
      fetch('./api/branch1.json'),
      fetch('./api/branch2.json'),
      fetch('./api/branch3.json'),
    ])
      .then((responses) => {
        return Promise.all(responses.map((response) => response.json()))
      })
      .then((data) => {
        let TransformedData = [
          ...data[0].products,
          ...data[1].products,
          ...data[2].products,
        ]

        console.log(TransformedData)

        const transformedData = TransformedData.map((productData, index) => {
          return {
            id: productData.id,
            key: index,
            productName: productData.name,
            unitPrice: productData.unitPrice,
            soldQuantity: productData.sold,
          }
        })
        console.log(transformedData)
        setData(transformedData)
        setIsLoading(false)
      })
  }, [])

  function sortOn(property) {
    return function (a, b) {
      if (a[property] < b[property]) {
        return -1
      } else if (a[property] > b[property]) {
        return 1
      } else {
        return 0
      }
    }
  }

  data.sort(sortOn('productName'))

  let reducedObjArr = Object.values(
    data.reduce((acc, curr) => {
      ;(acc[curr.productName] = acc[curr.productName] || {
        productName: curr.productName,
        id: curr.id,
        unitPrice: curr.unitPrice,
        soldQuantity: 0,
      }).soldQuantity += curr.soldQuantity
      return acc
    }, {})
  )

  function search(rows) {
    return rows.filter(
      (row) =>
        row.productName
          .toString()
          .toLowerCase()
          .indexOf(query.toString().toLowerCase()) > -1
    )
  }

  return (
    <div className="product-list">
      <div>
        <label>Search Products</label>
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
      </div>
      <div>
        {!isLoading && reducedObjArr.length > 0 &&<Datatable data={search(reducedObjArr)} />}
        {!isLoading && reducedObjArr.length === 0 && <p>Found no data!!</p>}
        {isLoading && <p>Loading...</p>}
      </div>
    </div>
  )
}

export default App
