import React, { useState, useEffect } from 'react'
import Datatable from './datatable'
import './App.css'

function App() {
  const [data, setData] = useState([])
  const [query, setQuery] = useState([])

  useEffect(() => {
    Promise.all([
      fetch('./api/branch1.json'),
      fetch('./api/branch2.json'),
      fetch('./api/branch3.json'),
    ])
      .then((responses) => {
        return Promise.all(responses.map((response) => response.json()))
      })
      .then((data) => {
        console.log(data[0].products,data[1].products);
        setData(data)
      })
  }, [])

  function sortOn(property) {
    return function (a, b) {
      if (a[property] < b[property]) {
        return -1
      } else if (a[property] > b[property]) {
        return 1
      } else {
        return 0
      }
    }
  }

  // let branch1 = data[0].products
  // let branch2 = data[1].products
  // let branch3 = data[2].products

  data.sort(sortOn('name'))


  let reducedObjArr = Object.values(
    data.reduce((acc, curr) => {
      ;(acc[curr.name] = acc[curr.name] || {
        name: curr.name,
        id: curr.id,
        unitPrice: curr.unitPrice,
        sold: 0,
      }).sold += curr.sold
      return acc
    }, {})
  )

  function search(rows) {
    return rows.filter(
      (row) =>
        row.name
          .toString()
          .toLowerCase()
          .indexOf(query.toString().toLowerCase()) > -1
    )
  }

  return (
    <div className="product-list">
      <div>
        <label>Search Products</label>
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
      </div>
      <div>
        <Datatable data={search(reducedObjArr)} />
      </div>
    </div>
  )
}

export default App
