import React from 'react'

const Datatable = (props) => {
  function findTotal(prop) {
    return (
      parseFloat(prop.unitPrice, 2) * parseFloat(prop.soldQuantity, 2)
    ).toFixed(2)
  }
  const RevenueValues = props.data.map(findTotal)

  console.log(RevenueValues)

  let Total = RevenueValues.reduce(function (accumulator, current) {
    return parseFloat(accumulator,2) + parseFloat(current,2)
  })
 

  console.log(Total)

  return (
    <table cellPadding={0} cellSpacing={0}>
      <thead>
        <tr>
          <th>Product</th>
          <th>Revenue</th>
        </tr>
      </thead>
      <tbody>
        {props.data.map((prop) => (
          <tr>
            <td>{prop.productName}</td>
            <td>
              {(
                parseFloat(prop.unitPrice, 2) * parseFloat(prop.soldQuantity, 2)
              ).toFixed(2)}
            </td>
          </tr>
        ))}
      </tbody>
      <tfoot>
        <tr>
          <td>Total</td>
          <td></td>
        </tr>
      </tfoot>
    </table>
  )
}

export default Datatable
