import React, { useState, useEffect } from 'react'
import Datatable from './datatable'
import branch1 from '../public/api/branch1.json'
import branch2 from '../public/api/branch2.json'
import branch3 from '../public/api/branch3.json'
import './App.css'

console.log(branch1)

function App() {
  const [data, setData] = useState([])
  const [query, setQuery] = useState([])

  useEffect(() => {
    Promise.all([
      fetch('./api/branch1.json'),
      fetch('./api/branch2.json'),
      fetch('./api/branch3.json'),
    ])
      .then((responses) => {
        return Promise.all(responses.map(function (response) {
          return response.json();
        }))
      })
      .then((data) => {
        const Data = data.products
        const transformedData = Data.map((product) => {
          return {
            id: product.id,
            key: Math.random(),
            productName: product.name,
            unitPrice: product.unitPrice,
            soldQuantity: product.sold,
          }
        })
        console.log(transformedData)
        setData(transformedData)
      })
  }, [])

  const map = new Map()

  const BranchValues = [...branch1, ...branch2, ...branch3]
  console.log(BranchValues)

  function sortOn(property) {
    return function (a, b) {
      if (a[property] < b[property]) {
        return -1
      } else if (a[property] > b[property]) {
        return 1
      } else {
        return 0
      }
    }
  }

  transformedData.sort(sortOn('name'))

  let reducedObjArr = Object.values(
    transformedData.reduce((acc, curr) => {
      ;(acc[curr.name] = acc[curr.name] || {
        name: curr.name,
        id: curr.id,
        unitPrice: curr.unitPrice,
        sold: 0,
      }).sold += curr.sold
      return acc
    }, {})
  )

  function search(rows) {
    return rows.filter(
      (row) =>
        row.name
          .toString()
          .toLowerCase()
          .indexOf(query.toString().toLowerCase()) > -1
    )
  }

  return (
    <div className="product-list">
      <div>
        <label>Search Products</label>
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
      </div>
      <div>
        <Datatable data={search(transformedData)} />
      </div>
    </div>
  )
}

export default App
