import React from 'react'

const Datatable = (props) => {
      
    const Total = props.data.map(findTotal)    
  
    function findTotal(prop) {
        let total = 0;
        total += parseFloat(parseFloat(prop.unitPrice,2) * parseFloat(prop.soldQuantity,2),2).toFixed(2)
        return total;
    }

    console.log(Total);

  return (
    <table cellPadding={0} cellSpacing={0}>
      <thead>
        <tr>
          <th>Product</th>
          <th>Revenue</th>
        </tr>
      </thead>
      <tbody>
        {props.data.map((prop) => (
          <tr>
            <td>{prop.productName}</td>
            <td>
              {(
                parseFloat(prop.unitPrice, 2) * parseFloat(prop.soldQuantity, 2)
              ).toFixed(2)}
            </td>
          </tr>
        ))}
      </tbody>
      <tfoot>
        <tr>
          <td>Total</td>
          <td></td>
        </tr>
      </tfoot>
    </table>
  )
}

export default Datatable
