import React, { useState, useEffect } from 'react'
import Datatable from './datatable'
import './App.css'

function App() {
  const [data, setData] = useState([])
  const [query, setQuery] = useState([])

  useEffect(() => {
    Promise.all([
      fetch('./api/branch1.json'),
      fetch('./api/branch2.json'),
      fetch('./api/branch3.json'),
    ])
      .then((responses) => {
        return Promise.all(
          responses.map(function (response) {
            return response.json()
          })
        )
      })
      .then((Data) => {
        setData(Data)
      })
  }, [])



  function sortOn(property) {
    return function (a, b) {
      if (a[property] < b[property]) {
        return -1
      } else if (a[property] > b[property]) {
        return 1
      } else {
        return 0
      }
    }
  }

  // data = data[0].concat(data[1]).concat(data[2])
  data.sort(sortOn('name'))
  console.log(data,data[0].Products)

  let reducedObjArr = Object.values(
    data.reduce((acc, curr) => {
      ;(acc[curr.name] = acc[curr.name] || {
        name: curr.name,
        id: curr.id,
        unitPrice: curr.unitPrice,
        sold: 0,
      }).sold += curr.sold
      return acc
    }, {})
  )


  function search(rows) {
    return rows.filter(
      (row) =>
        row.name
          .toString()
          .toLowerCase()
          .indexOf(query.toString().toLowerCase()) > -1
    )
  }

  return (
    <div className="product-list">
      <div>
        <label>Search Products</label>
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
      </div>
      <div>
        <Datatable data={search(data)} />
      </div>
    </div>
  )
}

export default App
