import React, { useState, useEffect } from 'react'
import Datatable from './datatable'
import './App.css'


function App() {
  const [data, setData] = useState([])
  const [query, setQuery] = useState([])

  useEffect(() => {
    Promise.all([
      fetch('./api/branch1.json'),
      fetch('./api/branch2.json'),
      fetch('./api/branch3.json'),
    ])
      .then((responses) => {
        return Promise.all(responses.map(function (response) {
          return response.json();
        }))
      })
      .then((data) => {

        const transformedData = data.map((branch) => branch.map((product) => (
           {
            id: product.id,
            key: Math.random().toFixed(2),
            productName: product.name,
            unitPrice: product.unitPrice,
            soldQuantity: product.sold,
          }
        )))
        console.log(transformedData)
        setData(transformedData)
      })
  }, [])

  const map = new Map()

  function sortOn(property) {
    return function (a, b) {
      if (a[property] < b[property]) {
        return -1
      } else if (a[property] > b[property]) {
        return 1
      } else {
        return 0
      }
    }
  }

  data.sort(sortOn('name'))

  let reducedObjArr = Object.values(
    data.reduce((acc, curr) => {
      ;(acc[curr.name] = acc[curr.name] || {
        name: curr.name,
        id: curr.id,
        unitPrice: curr.unitPrice,
        sold: 0,
      }).sold += curr.sold
      return acc
    }, {})
  )

  function search(rows) {
    return rows.filter(
      (row) =>
        row.name
          .toString()
          .toLowerCase()
          .indexOf(query.toString().toLowerCase()) > -1
    )
  }

  return (
    <div className="product-list">
      <div>
        <label>Search Products</label>
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
      </div>
      <div>
        <Datatable data={search(data)} />
      </div>
    </div>
  )
}

export default App
