        productName: curr.productName,
        id: curr.id,
        unitPrice: curr.unitPrice,
        soldQuantity: 0,
      }).soldQuantity += curr.soldQuantity
      return acc
    }, {})
  )

  function search(rows) {
    return rows.filter(
      (row) =>
        row.productName
          .toString()
          .toLowerCase()
          .indexOf(query.toString().toLowerCase()) > -1
    )
  }

  return (
    <div className="product-list">
      <div>{isLoading && <text className="loading">Loading...</text>}</div>

      <div>
        {!isLoading && reducedObjArr.length > 0 && (
          <SearchDiv
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
          />
        )}
        {!isLoading && reducedObjArr.length > 0 && (
          <Datatable data={search(reducedObjArr)} />
        )}
        {!isLoading && reducedObjArr.length === 0 && (
          <text className="loading">Found no data!!</text>
        )}
      </div>
    </div>
  )
}

export default App
