import React, { useState, useEffect } from 'react'
import Datatable from './datatable'
import Branch1 from './api/branch1.json'
import Branch2 from './api/branch2.json'
import Branch3 from './api/branch3.json'
import './App.css'

function App() {
  const [data, setData] = useState([])
  const [query, setQuery] = useState([])

  const BranchValues = Branch1.concat(Branch2).concat(Branch3)
  console.log(BranchValues);

  useEffect(() => {
    fetch('./api/branch1.json')
      .then((response) => {
        return response.json()
      })
      .then((data) => {
        const Data = (data.products)
        const transformedData = Data.map((product) => {
          return {
            id: product.id,
            key: product.id,
            productName: product.name,
            unitPrice: product.unitPrice,
            soldQuantity: product.sold,
          }
        })
        console.log(transformedData)
        setData(transformedData)
      })
  }, [])




  function sortOn(property){
    return function(a, b){
        if(a[property] < b[property]){
            return -1;
        }else if(a[property] > b[property]){
            return 1;
        }else{
            return 0;   
        }
    }
  }

  data.sort(sortOn("productName"))

  return (
    <div className="product-list">
      <div>
        <label>Search Products</label>
        <input type="text" />
      </div>
      <div>
        <Datatable data={data} />
      </div>
    </div>
  )
}


export default App
